import { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './App.css';

const API_KEY = "sk-proj-X3iYsXxKA8x5RRAk9tnkT3BlbkFJ6pMn5eFKnfeaDs7BNfvn";

function ResumeForm() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    experience: '',
    education: '',
    skills: ''
  });

  const [resume, setResume] = useState('');
  const [setIsLoading] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (resume && isEditing) {
      document.getElementById("editable-resume").focus();
    }
  }, [resume, isEditing]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleResumeChange = (e) => {
    setResume(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    const apiRequestBody = {
      "model": "gpt-3.5-turbo",
      "messages": [
        { "role": "system", "content": "Generate a professional resume based on the following information." },
        { "role": "user", "content": JSON.stringify(formData) }
      ]
    };

    await fetch("https://api.openai.com/v1/chat/completions", {
      method: "POST",
      headers: {
        "Authorization": "Bearer " + API_KEY,
        "Content-Type": "application/json"
      },
      body: JSON.stringify(apiRequestBody)
    })
    .then((response) => response.json())
    .then((data) => {
      const generatedResume = data.choices[0].message.content;
      setResume(generatedResume);
      setIsLoading(false);
      setIsEditing(true); // Allow editing after resume is generated
    });
  };

  const handleSave = () => {
    const savedResumes = JSON.parse(localStorage.getItem('resumes')) || [];
    const newResume = { id: Date.now(), resume }; // Only save the generated resume
    savedResumes.push(newResume);
    localStorage.setItem('resumes', JSON.stringify(savedResumes));
    navigate(`/resumes#resume-${newResume.id}`);
  };

  const handleRegenerate = async () => {
    setIsLoading(true);
    setResume('');
    setIsEditing(false); // Switch to non-editable mode

    const apiRequestBody = {
      "model": "gpt-3.5-turbo",
      "messages": [
        { "role": "system", "content": "Generate a professional resume based on the following information." },
        { "role": "user", "content": JSON.stringify(formData) }
      ]
    };

    await fetch("https://api.openai.com/v1/chat/completions", {
      method: "POST",
      headers: {
        "Authorization": "Bearer " + API_KEY,
        "Content-Type": "application/json"
      },
      body: JSON.stringify(apiRequestBody)
    })
    .then((response) => response.json())
    .then((data) => {
      const generatedResume = data.choices[0].message.content;
      setResume(generatedResume);
      setIsLoading(false);
    });
  };

  const handleDelete = () => {
    setResume('');
    setIsLoading(false);
    setIsEditing(true); // Switch back to editable mode
  };

  return (
    <div className="App">
      {!resume || !isEditing ? (
        <form className="resume-form" onSubmit={handleSubmit}>
          <h1>Resume Generator</h1>
          <label>
            First Name:
            <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} required />
          </label>
          <label>
            Last Name:
            <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} required />
          </label>
          <label>
            Email:
            <input type="email" name="email" value={formData.email} onChange={handleChange} required />
          </label>
          <label>
            Phone:
            <input type="tel" name="phone" value={formData.phone} onChange={handleChange} required />
          </label>
          <label>
            Experience:
            <textarea name="experience" value={formData.experience} onChange={handleChange} required></textarea>
          </label>
          <label>
            Education:
            <textarea name="education" value={formData.education} onChange={handleChange} required></textarea>
          </label>
          <label>
            Skills:
            <textarea name="skills" value={formData.skills} onChange={handleChange} required></textarea>
          </label>
          <button type="submit">Generate Resume</button>
        </form>
      ) : (
        <div className="resume-output">
          <h2>Generated Resume:</h2>
          <textarea
            id="editable-resume"
            name="resume"
            value={resume}
            onChange={handleResumeChange}
            readOnly={!isEditing}
            required
            autoFocus={isEditing}
          ></textarea>
          <div className="resume-actions">
            <button onClick={handleSave}>Save</button>
            <button onClick={handleRegenerate}>Regenerate</button>
            <button onClick={handleDelete}>Delete</button>
          </div>
        </div>
      )}
      <Link to="/resumes" className="link">View Saved Resumes</Link>
    </div>
  );
}

export default ResumeForm;
