import { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './App.css';
import './animations.css';

function ResumeList() {
  const [resumes, setResumes] = useState([]);
  const [collapsed, setCollapsed] = useState({});
  const location = useLocation();

  useEffect(() => {
    const savedResumes = JSON.parse(localStorage.getItem('resumes')) || [];
    setResumes(savedResumes);

    // Scroll to the saved resume if redirected with hash
    if (location.hash) {
      const element = document.querySelector(location.hash);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    }
  }, [location]);

  const handleDelete = (id) => {
    const updatedResumes = resumes.filter((resume) => resume.id !== id);
    setResumes(updatedResumes);
    localStorage.setItem('resumes', JSON.stringify(updatedResumes));
  };

  const toggleCollapse = (id) => {
    console.log(`Toggling collapse for resume id: ${id}`);
    setCollapsed((prevState) => ({
      ...prevState,
      [id]: !prevState[id],
    }));
  };

  return (
    <div className="App">
      <h1>Saved Resumes</h1>
      <div className="resume-list">
        {resumes.map((resume) => (
          <div key={resume.id} className={`resume-item ${collapsed[resume.id] ? 'collapsed' : 'expanded'}`} id={`resume-${resume.id}`}>
            <pre>{resume.resume}</pre>
            <div className="resume-actions">
              <button onClick={() => toggleCollapse(resume.id)}>
                {collapsed[resume.id] ? 'Expand' : 'Collapse'}
              </button>
              <Link to={`/edit/${resume.id}`}>Edit</Link>
              <button onClick={() => handleDelete(resume.id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
      <Link to="/" className="link">Back to Form</Link>
    </div>
  );
}

export default ResumeList;
