// ResumeList.jsx

import { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './App.css';

function ResumeList() {
  const [resumes, setResumes] = useState([]);
  const [collapsed, setCollapsed] = useState({});
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const savedResumes = JSON.parse(localStorage.getItem('resumes')) || [];
    setResumes(savedResumes);

    if (location.hash) {
      const element = document.querySelector(location.hash);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    }
  }, [location]);

  const handleDelete = (id) => {
    const updatedResumes = resumes.filter((resume) => resume.id !== id);
    setResumes(updatedResumes);
    localStorage.setItem('resumes', JSON.stringify(updatedResumes));
  };

  const toggleCollapse = (id) => {
    const element = document.getElementById(`resume-${id}`);
    if (collapsed[id]) {
      expandElement(element);
    } else {
      collapseElement(element);
    }
    setCollapsed((prevState) => ({
      ...prevState,
      [id]: !prevState[id],
    }));
  };

  const collapseElement = (element) => {
    const sectionHeight = element.scrollHeight;
    const elementTransition = element.style.transition;
    element.style.transition = '';
    requestAnimationFrame(() => {
      element.style.height = `${sectionHeight}px`;
      element.style.transition = elementTransition;
      requestAnimationFrame(() => {
        element.style.height = '150px';
      });
    });
  };

  const expandElement = (element) => {
    const sectionHeight = element.scrollHeight;
    element.style.height = `${sectionHeight}px`;
    element.addEventListener('transitionend', function handler(e) {
      element.removeEventListener('transitionend', handler);
      element.style.height = null;
    });
  };

  return (
    <div className="ResumeList">
      <h1 className="ResumeList-title">Saved Resumes</h1>
      <button onClick={() => navigate('/')} className="ResumeList-link-button">Back to Resume Generator</button>
      {resumes.length === 0 ? (
        <p>No resumes saved yet.</p>
      ) : (
        <ul className="resume-list">
          {resumes.map((resume) => (
            <li key={resume.id} className="resume-item">
              <textarea
                id={`resume-${resume.id}`}
                className="saved-resume"
                value={resume.resume}
                readOnly
                rows={collapsed[resume.id] ? '4' : undefined}
              ></textarea>
              <div className="resume-actions">
                <button onClick={() => toggleCollapse(resume.id)} className="ResumeList-button">
                  {collapsed[resume.id] ? 'Expand' : 'Collapse'}
                </button>
                <button onClick={() => handleDelete(resume.id)} className="ResumeList-button">Delete</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ResumeList;
