// EditResume.jsx

import { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './App.css';

function EditResume() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [resume, setResume] = useState('');
  const textareaRef = useRef(null);

  useEffect(() => {
    const savedResumes = JSON.parse(localStorage.getItem('resumes')) || [];
    const resumeToEdit = savedResumes.find((resume) => resume.id === parseInt(id));
    if (resumeToEdit) {
      setResume(resumeToEdit.resume);
    }
  }, [id]);

  useEffect(() => {
    if (textareaRef.current) {
      autoResizeTextarea();
    }
  }, [resume]);

  const autoResizeTextarea = () => {
    const textarea = textareaRef.current;
    textarea.style.height = 'auto';
    textarea.style.height = `${textarea.scrollHeight}px`;
  };

  const handleChange = (e) => {
    setResume(e.target.value);
    autoResizeTextarea();
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const savedResumes = JSON.parse(localStorage.getItem('resumes')) || [];
    const updatedResumes = savedResumes.map((savedResume) =>
      savedResume.id === parseInt(id) ? { ...savedResume, resume } : savedResume
    );
    localStorage.setItem('resumes', JSON.stringify(updatedResumes));
    navigate(`/resumes#resume-${id}`);
  };

  const handleCancel = () => {
    navigate('/resumes');
  };

  return (
    <div className="App EditResume">
      <form className="edit-resume-form" onSubmit={handleSubmit}>
        <h1 className="EditResume-title">Edit Resume</h1>
        <label className="EditResume-label">
          Resume:
          <textarea
            ref={textareaRef}
            name="resume"
            value={resume}
            onChange={handleChange}
            required
          ></textarea>
        </label>
        <div className="edit-resume-formButton">
          <button type="submit" className="EditResume-button">Save Changes</button>
          <button type="button" onClick={handleCancel} className="EditResume-button">Cancel Editing</button>
        </div>
      </form>
    </div>
  );
}

export default EditResume;
