// EditResume.jsx
import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './App.css';

function EditResume() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [resume, setResume] = useState('');
  const [originalResume, setOriginalResume] = useState('');

  useEffect(() => {
    const savedResumes = JSON.parse(localStorage.getItem('resumes')) || [];
    const resumeToEdit = savedResumes.find((resume) => resume.id === parseInt(id));
    if (resumeToEdit) {
      setResume(resumeToEdit.resume);
      setOriginalResume(resumeToEdit.resume);
    }
  }, [id]);

  const handleChange = (e) => {
    setResume(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const savedResumes = JSON.parse(localStorage.getItem('resumes')) || [];
    const updatedResumes = savedResumes.map((savedResume) =>
      savedResume.id === parseInt(id) ? { ...savedResume, resume } : savedResume
    );
    localStorage.setItem('resumes', JSON.stringify(updatedResumes));
    navigate(`/resumes#resume-${id}`);
  };

  const handleCancel = () => {
    navigate('/resumes');
  };

  return (
    <div className="App">
      <form className="resume-form" onSubmit={handleSubmit}>
        <h1>Edit Resume</h1>
        <label>
          Resume:
          <textarea name="resume" value={resume} onChange={handleChange} required></textarea>
        </label>
        <button type="submit">Save Changes</button>
        <button type="button" onClick={handleCancel}>Cancel Editing</button>
      </form>
    </div>
  );
}

export default EditResume;
