// EditResume.jsx

import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './App.css';

function EditResume() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    experience: '',
    education: '',
    skills: ''
  });

  useEffect(() => {
    const savedResumes = JSON.parse(localStorage.getItem('resumes')) || [];
    const resumeToEdit = savedResumes.find((resume) => resume.id === parseInt(id));
    if (resumeToEdit) {
      setFormData(resumeToEdit); // Set all form data including generated resume
    }
  }, [id]);
  

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const savedResumes = JSON.parse(localStorage.getItem('resumes')) || [];
    const updatedResumes = savedResumes.map((resume) =>
      resume.id === parseInt(id) ? { ...formData, resume: resume.resume } : resume
    );
    localStorage.setItem('resumes', JSON.stringify(updatedResumes));
    navigate('/resumes');
  };

  return (
    <div className="App">
      <form className="resume-form" onSubmit={handleSubmit}>
        <h1>Edit Resume</h1>
        <label>
          First Name:
          <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} required />
        </label>
        <label>
          Last Name:
          <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} required />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </label>
        <label>
          Phone:
          <input type="tel" name="phone" value={formData.phone} onChange={handleChange} required />
        </label>
        <label>
          Experience:
          <textarea name="experience" value={formData.experience} onChange={handleChange} required></textarea>
        </label>
        <label>
          Education:
          <textarea name="education" value={formData.education} onChange={handleChange} required></textarea>
        </label>
        <label>
          Skills:
          <textarea name="skills" value={formData.skills} onChange={handleChange} required></textarea>
        </label>
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
}

export default EditResume;
